@charset "UTF-8";
/* ===== 基础字体与页面默认样式 ===== */
html {
  font-size: 16px; /* 默认根字体大小，一般浏览器是 16px */
  line-height: 1.5; /* 行高 */
}

body {
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif; /* 类似浏览器默认字体 */
  font-size: 1rem; /* 16px */
  line-height: 1.5;
  color: #333; /* 默认文字颜色 */
  background-color: #fff; /* 默认背景白色 */
  margin: 0;
  padding: 1px 0 1px 0;
}

/* ===== 标题样式 ===== */
h1, h2, h3, h4, h5, h6 {
  font-weight: bold;
  line-height: 1.2;
  margin: 0.67em 0; /* 类似浏览器默认上下边距 */
}

h1 {
  font-size: 2em;
}

h2 {
  font-size: 1.5em;
}

h3 {
  font-size: 1.17em;
}

h4 {
  font-size: 1em;
}

h5 {
  font-size: 0.83em;
}

h6 {
  font-size: 0.67em;
}

/* ===== 段落样式 ===== */
p {
  margin: 1em 0;
}

/* ===== 链接样式 ===== */
a {
  color: #0066cc; /* 类似浏览器默认蓝色链接 */
  text-decoration: underline;
}

a:hover {
  text-decoration: underline;
}

/* ===== 列表样式 ===== */
ul, ol {
  margin: 1em 0;
  padding-left: 2em; /* 缩进，类似浏览器默认列表 */
}

li {
  line-height: 1.5;
}

ul {
  list-style-type: disc; /* 实心圆点 */
}

ol {
  list-style-type: decimal; /* 数字 */
}

/* ===== 强调标签 ===== */
strong, b {
  font-weight: bold;
}

em, i {
  font-style: italic;
}

/* ===== 引用块 ===== */
blockquote {
  margin: 1em 0;
  padding-left: 1em;
  border-left: 4px solid #ccc;
  color: #666;
}

/* ===== 代码块与行内代码 ===== */
code {
  font-family: Consolas, Monaco, "Andale Mono", monospace;
  background-color: #f4f4f4;
  padding: 0.1em 0.3em;
  border-radius: 3px;
  font-size: 0.9em;
}

pre {
  font-family: Consolas, Monaco, "Andale Mono", monospace;
  background-color: #f4f4f4;
  padding: 1em;
  border-radius: 4px;
  overflow-x: auto;
  margin: 1em 0;
}

pre code {
  background: none;
  padding: 0;
}

/* ===== 表单元素 ===== */
input, textarea, button, select {
  font-family: inherit;
  font-size: 1rem;
  padding: 0.4em 0.6em;
  margin: 0.2em 0;
  border: 1px solid #ccc;
  border-radius: 4px;
  background-color: #fff;
}

button {
  cursor: pointer;
  background-color: #f0f0f0;
  color: #333;
}

button:hover {
  background-color: #e0e0e0;
}

/* ===== 表格（可选） ===== */
table {
  border-collapse: collapse;
  width: 100%;
  margin: 1em 0;
}

th, td {
  border: 1px solid #ccc;
  padding: 0.5em;
  text-align: left;
}

th {
  background-color: #f4f4f4;
  font-weight: bold;
}

