appsmith.onReady(() => {
  /*
   * This handler function will get called when parent application is ready.
   * Initialize your component here
   * more info - https://docs.appsmith.com/reference/widgets/custom#onready
   */
  new Vue({
    el: "#app",
    data() {
      return {
        content: appsmith.model.content
      };
    },
    computed: {
      compiledMarkdown() {
        return marked.parse(this.content.text);
      }
    },
    created() {
      const unlisten = appsmith.onModelChange(newModel => {
        console.log('newModel', newModel);
        console.log(JSON.parse(decodeURIComponent(newModel.content)));
        this.content = JSON.parse(decodeURIComponent(newModel.content));
      });
    }
  });
});

