#app, div {
  background: transparent;
}

.flex-box {
  display: flex;
  flex-wrap: wrap;
}

.img-box {
  margin-right: 10px;
  position: relative;
}

.img {
  height: 100px;
  border-radius: 4px;
  cursor: pointer;
}

.delete-btn {
  width: 20px;
  height: 20px;
  border-radius: 10px;
  position: absolute;
  top: 2px;
  right: 2px;
  cursor: pointer;
  text-align: center;
  line-height: 20px;
  user-select: none;
}

.delete-btn:hover {
  background-color: rgba(255, 255, 255, 0.1);
  transition: all 0.3s;
}

