appsmith.onReady(() => {
  /*
   * This handler function will get called when parent application is ready.
   * Initialize your component here
   * more info - https://docs.appsmith.com/reference/widgets/custom#onready
   */
  new Vue({
    el: "#app",
    data() {
      return {
        fileList: appsmith.model.fileList
      };
    },
    methods: {
      deleteClick(index) {
        appsmith.triggerEvent("deleteClick", {
          index
        });
      },
      ImgClick(index) {
        appsmith.triggerEvent("ImgClick", {
          index
        });
      }
    },
    created() {
      const unlisten = appsmith.onModelChange(newModel => {
        this.fileList = JSON.parse(newModel.fileList);
      });
    }
  });
});

